def get_ip(def hostname) { def host_list = "" def data = readFile(file: '/var/lib/jenkins/openstack/ip_hostname') data = data.split('\n') hosts = hostname.split(',') for ( def i = 0 ; i < hosts.length ; i++ ) { for ( def j = 0 ; j < data.length ; j++ ){ if ( data[j].contains(hosts[i]) ) { host_list = host_list + data[j] + ',' break; } } } host_list = host_list.substring(0,host_list.length() - 1) return host_list //println host_list }
