} readdata.grafana_hosts = "$abc3" }
String abc5 = "" if ( LoggingNodes != "{}") { nameippair = LoggingNodes.split(',') for (def a in nameippair ) { subArray = a.split(':') key = subArray[0] value = subArray[1] abc5 = abc5 + key + ":\n" + " ip: " + value + "\n"
