stage('Colne the repository') { if ( OSACloning == "true" ) {
println "Passed deployment IP : $DeploymentNodeIp"
sh """
ssh -i $JnekinsPrivateKey -o StrictHostKeyChecking=no root@$DeploymentNodeIp "grep http_proxy /etc/environment || echo http_proxy=$ProxyUrl >>/etc/environment; grep https_proxy /etc/environment || echo https_proxy=$ProxyUrl >> /etc/environment" #ssh -o StrictHostKeyChecking=no -i $JnekinsPrivateKey root@$DeploymentNodeIp 'rm -rf /opt/openstack-ansible' ssh -o StrictHostKeyChecking=no -i $JnekinsPrivateKey root@$DeploymentNodeIp 'if [ -d /opt/openstack-ansible ]; then echo "Directory exits" ; else git clone -b $OSABranch $OSARepository $OsaCd ; fi'
"""
} else { println "Not Cloning the repository as OSACloning was set to false " }
}
stage('Anisble_bootstraping') {
println "This stage will bootstrap and copy etc directory along with secret generation for OSA" if ( OSACloning == "true" ) {
sh """ ssh -o StrictHostKeyChecking=no -i $JnekinsPrivateKey root@$DeploymentNodeIp 'if [ -f /usr/local/bin/openstack-ansible ] ; then echo "Openstack already bootstrapped"; else cd /opt/openstack-ansible/scripts && ./bootstrap-ansible.sh ; fi' ssh -o StrictHostKeyChecking=no -i $JnekinsPrivateKey root@$DeploymentNodeIp 'if [ -f /etc/openstack_deploy/user_secrets.yml ]; then echo "File exists" ; else cp -pr /opt/openstack-ansible/etc/openstack_deploy /etc/ && cd /opt/openstack-ansible && ./scripts/pw-token-gen.py --file /etc/openstack_deploy/user_secrets.yml ; fi'
""" } else { println "Not boorstapping as OSACloning is set as false" } }
